Column.formatter:
function(value,row){
          if(value=='0'){
            return '工作中';
          }else if(value=='1')
          {
            return '请假中';
          }
          else if(value=='2')
          {
            return '已锁定';
          }
      }

Column.styler:
function(value){
          if (value == '2')
          {
              return 'color:red;';
          }else if (value == '1'){
             return 'color:Yellow;';
         }
         else if (value == '0'){
             return 'color:green;';
         }
      }
