Column.formatter:
function (v) {
                             if (v == '0') {
                                 return '工具栏';
                             }
                             else if (v == '1') {
                                 return '列表操作栏';
                             }
                             else {
                                 return '';
                             }
                         }

function (v) {
                             if (v == '1') {
                                 return '链接';
                             }
                             else if (v == '2') {
                                 return '事件';
                             }
                             else if (v == '3') {
                                 return '弹窗';
                             }
                             else if (v == '4') {
                                 return '新页面';
                             }
                             else if (v == '5') {
                                 return '自定义';
                             }
                             else {
                                 return '';
                             }
                         }
