Column.formatter:
function (v) {
                             if (v == '0') {
                                 return '待指派';
                             }
                             else if (v == '1') {
                                 return '已指派';
                             }
                             else if (v == '2') {
                                 return '锁定';
                             }
                             else if (v == '3') {
                                 return '终止';
                             }
                             else {
                                 return '';
                             }
}
